# QuoteSync - Fix Customers JSX (line ~1288) by full App.tsx overwrite
# Run from: PS C:\Github\QuoteSync\web\ps1_patches>
# Writes src\App.tsx from embedded Base64 payload.
# This script uses a temp file inside the project to avoid path issues.

$ErrorActionPreference = "Stop"
function Ok($m){ Write-Host "OK: $m" -ForegroundColor Green }
function Fail($m){ Write-Host "ERROR: $m" -ForegroundColor Red; throw $m }

Write-Host "Run directory: $((Get-Location).Path)"

# Ensure we end up in web root
if ((Get-Location).Path -like "*\ps1_patches") {
  Set-Location (Resolve-Path "..").Path
}
$webRoot = (Get-Location).Path
Write-Host "Working directory: $webRoot"

$appOut = Join-Path $webRoot "src\App.tsx"
if (-not (Test-Path $appOut)) { Fail "Expected $appOut. Run from PS C:\Github\QuoteSync\web\ps1_patches>" }

# Backup
$backupDir = Join-Path $webRoot "_backups"
New-Item -ItemType Directory -Force -Path $backupDir | Out-Null
$ts = Get-Date -Format "yyyyMMdd_HHmmss"
$bak = Join-Path $backupDir ("App.tsx.$ts.bak")
Copy-Item $appOut $bak -Force
Ok "Backup written: $bak"

# Temp dir inside project
$tmpDir = Join-Path $webRoot "_tmp"
New-Item -ItemType Directory -Force -Path $tmpDir | Out-Null
$tmp = Join-Path $tmpDir "App.tsx.__tmp__"

# Decode payload
$b64 = @"

"@.Trim()

try {
  $bytes = [System.Convert]::FromBase64String($b64)
} catch {
  Fail "Base64 decode failed: $($_.Exception.Message)"
}

[System.IO.File]::WriteAllBytes($tmp, $bytes)

# Atomic replace
Move-Item -Force -Path $tmp -Destination $appOut

Ok "src\App.tsx updated."
Ok "Done."
